  <script>
    document.addEventListener('DOMContentLoaded', () => {
      const sel = document.getElementById('filtro-empresa');
      function filtrarEmpresa() {
        const val = sel.value.toLowerCase().trim();
        document.querySelectorAll('tbody tr').forEach(r => {
          const emp = (r.getAttribute('data-empresa')||'').toLowerCase().trim();
          r.style.display = emp === val ? '' : 'none';
        });
      }
      sel.addEventListener('change', filtrarEmpresa);
      filtrarEmpresa();
    });
  </script>
